import React, { JSX, useState } from "react";
import "../../Assets/Fonts/LemonMilkRegularFont.css";
import AboutSection from "../Sections/About/AboutSection";
import ProjectSection from "../Sections/Projects/ProjectSection";
import FreelanceSection from "../Sections/Freelance/FreelanceSection";
import ContactSection from "../Sections/Contact/ContactSection";

type Section = "about" | "projects" | "contact" | "freelance";

const LayoutDynamic: React.FC = () => {
    const [section, setSection] = useState<Section>("about");

    const renderSection = (): JSX.Element => {
        switch (section) {
            case "about":
                return <AboutSection />;
            case "projects":
                return <ProjectSection />;
            case "contact":
                return <ContactSection />;
            case "freelance":
                return <FreelanceSection />;
            default: {
                const _exhaustiveCheck: never = section;
                throw new Error(`Unhandled section: ${_exhaustiveCheck}`);
            }
        }
    };

    return (
        <div className="layout-wrapper">
            <div className="container-fluid bg-dark text-light custom-layout-nav px-3 py-2 border-bottom border-3 border-warning">
                <nav className="d-flex justify-content-center align-items-center flex-wrap">
                    <div className="d-flex align-items-center mx-3">
                        <button onClick={() => setSection("about")} className="nav-btn lm-reg-font-m text-light">
                            About Me
                        </button>
                    </div>

                    <div className="d-flex align-items-center mx-3">
                        <i className="bi bi-bullseye text-warning fs-5 nav-icon-spacer" />
                    </div>

                    <div className="d-flex align-items-center mx-3">
                        <button onClick={() => setSection("projects")} className="nav-btn lm-reg-font-m text-light">
                            My Projects
                        </button>
                    </div>

                    <div className="d-flex align-items-center mx-3">
                        <i className="bi bi-bullseye text-warning fs-5 nav-icon-spacer" />
                    </div>

                    <div className="d-flex align-items-center mx-3">
                        <button onClick={() => setSection("freelance")} className="nav-btn lm-reg-font-m text-light">
                            Freelancing
                        </button>
                    </div>

                    <div className="d-flex align-items-center mx-3">
                        <i className="bi bi-bullseye text-warning fs-5 nav-icon-spacer" />
                    </div>

                    <div className="d-flex align-items-center mx-3">
                        <button onClick={() => setSection("contact")} className="nav-btn lm-reg-font-m text-light">
                            Contact Me
                        </button>
                    </div>
                </nav>
            </div>
            <div>{renderSection()}</div>
        </div>
    );
};

export default LayoutDynamic;
