import React, {useState} from "react";
import "../../../Assets/Fonts/LemonMilkRegularFont.css"
const ContactMe: React.FC = () => {
    // Initialize fields for formData
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        message: "",
    });

    const handleChange = (e: { target: { name: any; value: any; }; }) => {
        const { name, value } = e.target;
        // Update the state with the new value for the changed input field.
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e: { target: { name: any; value: any; }; }) => {
        e.preventDefault(); // Prevent page refresh

        try {
            const response = await fetch(
                "https://formspree.io/f/mrbpbqgn",
                {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify(formData), // Send the form data
                }
            );

            if (response.ok) {
                alert("Message sent successfully!");
                setFormData({ name: "", email: "", message: "" }); // Reset form
            } else {
                alert("Failed to send message.");
            }
        } catch (err) {
            console.error("Error occurred:", err);
            alert("An error occurred while sending your message.");
        }
    };

    return (
        <div className="container-fluid mt-5 py-4">
            <div className="card">
                <div className="card-header border border-warning border-3 text-center lm-bold-font">
                    Contact Me <i className="bi-phone-vibrate"></i>
                </div>
                <div className="card-body border border-warning border-3 text-center lm-bold-font">
                    <form>

                    </form>
                </div>


            </div>

        </div>
    );
};

export default ContactMe;
