import React, { useState } from "react";
import "../../../Assets/Fonts/LemonMilkRegularFont.css";

const ContactMe: React.FC = () => {
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        message: "",
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleSubmit: React.FormEventHandler<HTMLFormElement> = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch("https://formspree.io/f/mrbpbqgn", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                alert("üéâ Message sent successfully!");
                setFormData({ name: "", email: "", message: "" });
            } else {
                alert("‚ùå Failed to send message.");
            }
        } catch (err) {
            console.error("Error occurred:", err);
            alert("‚ö†Ô∏è An error occurred while sending your message.");
        }
    };

    return (
        <div className="container-fluid py-4">
            <div className="card shadow">
                <div className="card-header border border-warning border-3 text-center lm-bold-font">
                    Contact Me <i className="bi bi-phone-vibrate"></i>
                </div>
                <div className="card-body border border-warning border-3 lm-bold-font bg-light">
                    <form onSubmit={handleSubmit} className="mx-auto" style={{ maxWidth: "600px" }}>
                        <div className="mb-3 text-center">
                            <label htmlFor="name" className="form-label">Name</label>
                            <input
                                type="text"
                                className="form-control lato-p border-warning"
                                id="name"
                                name="name"
                                value={formData.name}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="mb-3 text-center">
                            <label htmlFor="email" className="form-label">Email</label>
                            <input
                                type="email"
                                className="form-control lato-p border-warning"
                                id="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="mb-3 text-center">
                            <label htmlFor="message" className="form-label">Message</label>
                            <textarea
                                className="form-control lato-p border-warning"
                                id="message"
                                name="message"
                                rows={4}
                                value={formData.message}
                                onChange={handleChange}
                                required
                            ></textarea>
                        </div>
                        <div className="text-center">
                            <button type="submit" className="btn btn-warning px-4">
                                Send
                            </button>
                        </div>
                    </form>
                </div>
                <div className="card-body"></div>
            </div>
        </div>
    );
};

export default ContactMe;
