import React from "react";

const AboutSection: React.FC = () => {
    return (
        <div className="container-fluid my-4" id="about">
            <div className="border border-warning border-3 rounded shadow p-4 bg-light">
                {/* Heading */}
                <h2 className="fs-4 fw-bold text-decoration-underline mb-4">
                    Welcome to my professional full-stack dev portfolio!
                </h2>

                {/* Content */}
                <div className="row align-items-center gx-4">
                    {/* Text Section */}
                    <div className="col-md-8">
                        <p className="text-muted fs-5">
                            I'm a <strong>backend-focused full-stack developer</strong> who builds <strong>scalable</strong>, <strong>efficient</strong> systems using <strong>ASP.NET Core</strong> and <strong>React</strong>. I specialize in <strong>clean architecture</strong>, <strong>robust APIs</strong>, and <strong>cloud-ready solutions</strong> with tools like <strong>EF Core</strong>, <strong>SQL Server</strong>, <strong>Docker</strong>, and <strong>Azure</strong>. While I enjoy front-end work with <strong>React</strong> and <strong>Blazor</strong>, my real strength lies in <strong>backend engineering</strong> — from <strong>Django</strong> in <strong>Python</strong> to high-performance systems in <strong>C#</strong>. Based in <strong>South Africa</strong>, I'm currently open to full-time opportunities in <strong>Cape Town</strong>, <strong>Pretoria</strong>, or <strong>Johannesburg</strong> — on-site, hybrid, or remote.
                        </p>


                    </div>

                    {/* Avatar Section */}
                    <div className="col-md-4 text-center mt-4 mt-md-0">
                        <img
                            src="https://StevenSerrao1.github.io/Official-Projects/assets/me.png"
                            alt="Steven's Avatar"
                            className="img-fluid rounded-circle border border-3 border-dark"
                            style={{ width: "205px", height: "205px", objectFit: "cover" }}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AboutSection;
