import React, {useState} from "react";
import "../../../Assets/Fonts/LemonMilkRegularFont.css"
const ContactMe: React.FC = () => {
    // Initialize fields for formData
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        message: "",
    });

    const handleChange = (e: { target: { name: any; value: any; }; }) => {
        const { name, value } = e.target;
        // Update the state with the new value for the changed input field.
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e: { target: { name: any; value: any; }; }) => {
        //e.preventDefault(); // Prevent page refresh

        try {
            const response = await fetch(
                "https://formspree.io/f/mrbpbqgn",
                {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify(formData), // Send the form data
                }
            );

            if (response.ok) {
                alert("Message sent successfully!");
                setFormData({ name: "", email: "", message: "" }); // Reset form
            } else {
                alert("Failed to send message.");
            }
        } catch (err) {
            console.error("Error occurred:", err);
            alert("An error occurred while sending your message.");
        }
    };

    return (
        <div className="container-fluid mt-5 py-4">
            <div className="card">
                <div className="card-header border border-warning border-3 text-center lm-bold-font">
                    Contact Me <i className="bi-phone-vibrate"></i>
                </div>
                <div className="card-body border border-warning border-3 text-center lm-bold-font">
                    <form onSubmit={handleSubmit} className="mx-auto" style={{ maxWidth: "600px" }}>
                        <div className="mb-3 text-start">
                            <label htmlFor="name" className="form-label">Name</label>
                            <input
                                type="text"
                                className="form-control"
                                id="name"
                                name="name"
                                value={formData.name}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="mb-3 text-start">
                            <label htmlFor="email" className="form-label">Email</label>
                            <input
                                type="email"
                                className="form-control"
                                id="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="mb-3 text-start">
                            <label htmlFor="message" className="form-label">Message</label>
                            <textarea
                                className="form-control"
                                id="message"
                                name="message"
                                rows={4}
                                value={formData.message}
                                onChange={handleChange}
                                required
                            ></textarea>
                        </div>
                        <div className="text-center">
                            <button type="submit" className="btn btn-warning px-4">
                                Send
                            </button>
                        </div>
                    </form>
                </div>


            </div>

        </div>
    );
};

export default ContactMe;
