import React, {useState} from "react";
import "../../../Assets/Fonts/LemonMilkRegularFont.css"
const ContactMe: React.FC = () => {
    // Initialize fields for formData
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        message: "",
    });

    const handleChange = (e: { target: { name: any; value: any; }; }) => {
        const { name, value } = e.target;
        // Update the state with the new value for the changed input field.
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e: { target: { name: any; value: any; }; }) => {
        //e.preventDefault(); // Prevent page refresh

        try {
            const response = await fetch(
                "https://formspree.io/f/mrbpbqgn",
                {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify(formData), // Send the form data
                }
            );

            if (response.ok) {
                alert("Message sent successfully!");
                setFormData({ name: "", email: "", message: "" }); // Reset form
            } else {
                alert("Failed to send message.");
            }
        } catch (err) {
            console.error("Error occurred:", err);
            alert("An error occurred while sending your message.");
        }
    };

    return (
        <div className="container-fluid mt-5 py-4">
            <div className="card">
                <div className="card-header border border-warning border-3 text-center lm-bold-font">
                    Contact Me <i className="bi-phone-vibrate"></i>
                </div>
                <div className="card-body border border-warning border-3 text-center lm-bold-font">
                    <form onSubmit={handleSubmit}>
                        {/* Name Input */}
                        <div className="mb-3">
                            <label htmlFor="name" className="form-label text-start lm-bold-font d-block">Name:</label>
                            <input
                                type="text"
                                className="form-control"
                                id="name"
                                name="name"
                                value={formData.name}
                                onChange={handleChange}
                                placeholder="Your Name"
                                required
                            />
                        </div>

                        {/* Email Input */}
                        <div className="mb-3">
                            <label htmlFor="email" className="form-label text-start lm-bold-font d-block">Email:</label>
                            <input
                                type="email"
                                className="form-control"
                                id="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                placeholder="your.email@example.com"
                                required
                            />
                        </div>

                        {/* Message Textarea */}
                        <div className="mb-3">
                            <label htmlFor="message" className="form-label text-start lm-bold-font d-block">Message:</label>
                            <textarea
                                className="form-control"
                                id="message"
                                name="message"
                                rows={5}
                                value={formData.message}
                                onChange={handleChange}
                                placeholder="Your message here..."
                                required
                            ></textarea>
                        </div>

                        {/* Submission Button */}
                        <button
                            type="submit"
                            className="btn btn-warning lm-bold-font w-100"
                            disabled={status === 'submitting'}
                        >
                            {status === 'submitting' ? (
                                <>
                                    <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
                                    {' '}Sending...
                                </>
                            ) : (
                                "Send Message"
                            )}
                        </button>

                        {/* Status/Response Message Display */}
                        {responseMessage && (
                            <div
                                className={`mt-3 alert ${status === 'success' ? 'alert-success' : 'alert-danger'}`}
                                role="alert"
                            >
                                {responseMessage}
                            </div>
                        )}
                    </form>
                </div>


            </div>

        </div>
    );
};

export default ContactMe;
