import React, { useState, useEffect } from "react";
import axios from "axios";
import { Image, Project } from "./types/interfaces"
import '../../../styles/card-styling.css'

const ProjectSection: React.FC = () => {
    const [projects, setProjects] = useState<Project[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        axios
            //.get('http://localhost:5000/api/projects/loadprojects') // Dev Endpoint
            .get('https://official-projects.onrender.com/api/projects/loadprojects') // Production Endpoint
            .then(res => {                  // If the request SUCCEEDS
                setProjects(res.data);                           // Shove data into projects[]
                setLoading(false);                              //  turn off the loading spinner
            })
            .catch(() =>                                     // ðŸ’¥ If the request fails for any reasonâ€¦
                setLoading(false)                              //    â€¢ still turn off the spinner (no infinite wait)
            );

    }, [])

    if (loading) {                                        // ðŸ¤” If weâ€™re still waiting on dataâ€¦
        return (                                           //    â€¢ bail out early and show a spinner
            <div className="d-flex justify-content-center my-5">
                <div className="spinner-border text-info" role="status">
                    <span className="visually-hidden">Loadingâ€¦</span>
                </div>
            </div>
        );
    }



    return (
        <div className="container-fluid my-4">
            <h2 className="fs-4 fw-bold text-decoration-underline mb-4">Freelance Development Services</h2>
        <div className="border border-warning border-3 rounded shadow p-4 bg-light">
        <div className="container py-5 mt-5">
            <div className="row g-4">
                {projects.map(p => (
                    <div key={p.images[0].projectId} className="col-12 col-sm-6 col-md-4">
                        <div className="card h-100 border-warning border-2 hover-lift">
                            {p.images?.[0] && (
                                <img
                                    src={p.images[0].imageUrl}
                                    alt={p.images[0].altText}
                                    className="card-img-top"
                                    style={{ height: '160px', objectFit: 'cover' }}
                                />
                            )}
                            <div className="card-body d-flex flex-column">
                                <h5 className="card-title border-bottom border-3 border-warning">{p.title}</h5>
                                <p className="card-text flex-grow-1">{p.shortDescription}</p>
                                <a
                                    href={p.projectURL}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="btn btn-sm btn-warning mt-auto"
                                >
                                    View
                                </a>
                                <a
                                    href={`https://github.com/StevenSerrao1/${p.gitHubRepoName}`}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="btn btn-sm btn-warning mt-2"
                                >
                                    Expand
                                </a>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
        </div>
        </div>
    );



}

export default ProjectSection;
